import React, { useState } from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import { Offcanvas, Image } from 'react-bootstrap';
import classes from './OffCanvas.module.css';
import cart_nav from "../../assets/cart-nav.svg";
import cart from "../../assets/cart.svg";

import Cart from '../Cart/Cart';


const OffCanvas = (props) => {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <>

        <div className={classes.nav_cart}  onClick={handleShow}>
            <Image src={cart_nav} fluid className={classes.nav_cart_img} width='10%'/>
            <p className={classes.badge}>{}</p>
        </div>

        <Offcanvas show={show} placement={'end'} onHide={handleClose}>
          <Offcanvas.Header closeButton>
          <Image src={cart} fluid className={classes.nav_cart_img} width='10%'/>

          </Offcanvas.Header>
          <Offcanvas.Body>
          <Cart/>
          </Offcanvas.Body>
        </Offcanvas>
      </>
    );
  }


export default OffCanvas
