import React, { useContext} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container , Row, Col, Image,} from 'react-bootstrap';
import AddButton from '../UI/AddButton/AddButton';
import CartContext from '../../store/cart-context';
import classes from './MenuItem.module.css';
import cart from "../../assets/cart.svg";
import heart from "../../assets/heart.svg";



const MenuItem = (props) => {

    const cartCtx = useContext(CartContext);

    const addToCartHandler = amount => {
        cartCtx.addItem({
            id: props.id,
            name: props.name,
            amount:amount,
            price: props.price
        });
    }

    return (
        <div>
            <Container>
                <Row>
                <li>
                                    <Col md={6} sm={12} key={props.id} id={props.id}>

                                            <div className={classes.card}>
                                                <div className={classes.menu_item}>
                                                    <Image src={props.image} fluid className={classes.menu_img}/>
                                                </div>
                                                <div className={classes.menu_item_inner}>
                                                    <div className={classes.menu_heart}>
                                                        <div className="py-3"></div>
                                                        <Image src={heart} className="d-block mx-auto" fluid width='50%'/>
                                                    </div>
                                                    <h1 className={classes.main_title}>{props.name}</h1>
                                                    <p>{props.description}</p>
                                                    <h2 className="pb-3">Price: <b>{props.price}</b></h2>
                                                    <div className={classes.chart_btn}>
                                                        <h2>Add <span><Image src={cart} fluid width='28%' /></span></h2>
                                                        <AddButton onAddToCart={addToCartHandler}/>
                                                    </div>
                                                </div>
                                            </div>

                                    </Col>            

                                    </li>


                </Row>
            </Container>
        </div>
    )
}

export default MenuItem;
