import React, { useState, useContext } from 'react';
import CartContext from '../../store/cart-contex';
import 'bootstrap/dist/css/bootstrap.min.css';
import {  Image, Offcanvas, Container, Row, Col  } from 'react-bootstrap';
import cart from "../../assets/cart-nav.svg";
import classes from './OffCanvasExample.module.css';
import MenuData from '../MenuData/MenuData';
import MenuItem from '../MenuItem/MenuItem';


const OffCanvasExample = (props) => {

    const cartCtx = useContext(CartContext);
    const numberOfCartItems = cartCtx.items.reduce((curNumber, item)=>{
      return curNumber = item.amount;
    }, 0);

    const cartItem = <ul className={classes['cart-items']}>
      {MenuData.map(item => (
        <li>{item.name}</li>
      ))}
    </ul>;

    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <>  
      <div className={classes.nav_cart} onClick={handleShow}>
        <Image src={cart} fluid className={classes.nav_cart_img}/>
        <p className={classes.badge}>{numberOfCartItems}</p>
      </div>

      <Offcanvas show={show} placement={'end'} onHide={handleClose} end>
        <Offcanvas.Header closeButton>
        <Offcanvas.Title>
          <h1>Your Cart</h1>
        </Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <Container>
            <Row>
              <Col></Col>
            </Row>
          </Container>
          {cartItem}
        </Offcanvas.Body>
      </Offcanvas>
    </>
    )
}

export default OffCanvasExample;
