import CartContext from '../../store/cart-context';
import 'bootstrap/dist/css/bootstrap.min.css';
// import classes from './Cart.module.css';
import { useCart } from 'react-use-cart';
import { Container, Row, Col, Image } from 'react-bootstrap';


const Cart = (props) => {
  const {
    isEmpty, 
    item, 
    totalItems,
    cartTotal,
    updateItemQuantity,
    removeItem, 
    emptyCart,
  } = useCart();
  if (isEmpty) return <h1>Your Cart is Empty</h1>
  return (
    <div>
      <Container>
        <Row>
          {totalItems.map((item, index) => {
            <Col key={index}>
              <div className={classes.card}>
                  <div className={classes.menu_item}>
                    <Image src={image} fluid className={classes.menu_img}/>
                  </div>
              <div className={classes.menu_item_inner}>
                <div className={classes.menu_heart}>
                <div className="py-3"></div>
                <Image src={heart} className="d-block mx-auto" fluid width='50%'/>
              </div>
                <h1 className={classes.main_title}>{name}</h1>
                <p>{description}</p>
                <h2 className="pb-3">Price: <b>{price}</b></h2>
                <div className={classes.chart_btn}>
                  <h2>Add <span><Image src={cart} fluid width='28%' /></span></h2>
                  <AddButton onClick={()=>addItem(props.item)}/>
                </div>
            </div>
          </div>
            </Col>
          })}

        </Row>
      </Container>
    </div>
  )

}

export default Cart;
