import React, { useState } from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import { Container , Row, Col, Image, Modal, Button  } from 'react-bootstrap';
import MenuItem from '../MenuItem/MenuItem';
import MenuData from '../MenuData/MenuData';


import classes from './CategoryGrid.module.css';

import breakfeast from "../../assets/breakfeast.png";
import lunch from "../../assets/lunch.png";
import dinner from "../../assets/dinner.png";
import pizza from "../../assets/pizza.png";
import burger from "../../assets/burgger.png";
import menu from "../../assets/menu.png";


const CategoryGrid = (props) => {
    const values = [true, 'sm-down', 'md-down', 'lg-down', 'xl-down', 'xxl-down'];
    const [fullscreen, setFullscreen] = useState(true);
    const [show, setShow] = useState(false);

    const [items, setItems]= useState(MenuData);
    const filterItem = (categItem) => {
        const updatedItems = MenuData.filter((curElem) => {
            return curElem.category === categItem;
        });

        setItems(updatedItems);

    }


    function handleShow(breakpoint) {
      setFullscreen(breakpoint);
      setShow(true);


    }



    return (
        <div>
           <Modal show={show} fullscreen={fullscreen} onHide={() => setShow(false)}>
                <Modal.Header closeButton>
                <Modal.Title>Our Menu</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <MenuItem/>
                </Modal.Body>
            </Modal>


            <Col >
                        {
                            items.map((elemnt) =>{
                                const {
                                    id, name, image, description, price } = elemnt;

                                    return (
                                        <div className={classes.card}>
                                            <Image  fluid className={classes.menu_img}/>
                                            <h1 className={classes.main_title}>{name}<span></span></h1>
                                            <p>{description}</p>
                                            <h4>{price}</h4>
                                            <Button  className={classes.custom_btn }>Order</Button>
                                        </div>
                                    )
                            })
                        }
                   </Col>

           <Container fluid>
               <Row>
                   <Col> 
                        <div className={classes.menu}>
                            <Image src={menu} fluid className={classes.menu_img}/>
                            <h1 className={classes.main_title}>Discover Our Menu<span></span></h1>
                            <Image src={menu} fluid className={classes.menu_img}/>


                        </div>
                   </Col>
               </Row>
               <Row>
                   <Col md={4} sm={12}>
                       <div className={classes.card}>
                       <Image src={breakfeast} fluid className={classes.card_img}/>
                       <div className={classes.card_content}>
                            <h1>Breakfeast</h1>
                            <h4>Lorem, ipsum dolor sit amet consectetur adipisicing elit.</h4>
                            <Button  className={classes.custom_btn } categoty='breakfeast' onClick={() => handleShow(values), () => filterItem('breakfeast') }>See the Menu</Button>
                        </div>
                       </div>

                   </Col>
                   <Col md={4} sm={12}>
                        <div className={classes.card}>
                            <Image src={lunch} fluid className={classes.card_img}/>
                            <div className={classes.card_content}>
                                <h1>Lunch</h1>
                                <h4>Lorem, ipsum dolor sit amet consectetur adipisicing elit.</h4>
                                <Button  className={classes.custom_btn }  onClick={() => handleShow(values)}>See the Menu</Button>    
                            </div>
                       </div>

                   </Col>
                   <Col md={4} sm={12}>
                        <div className={classes.card}>
                       <Image src={dinner} fluid className={classes.card_img}/>
                       <div className={classes.card_content}>
                            <h1>Dinner</h1>
                            <h4>Lorem, ipsum dolor sit amet consectetur adipisicing elit.</h4>
                            <Button  className={classes.custom_btn }  onClick={() => handleShow(values)}>See the Menu</Button>

                        </div>
                       </div>
                   </Col>

               </Row>
               <Row>
                    <Col md={6} sm={12}>
                    <div className={classes.card}>
                       <Image src={pizza} fluid className={classes.card_img}/>
                       <div className={classes.card_content}>
                            <h1 className={classes.card_content_title}>Pizza</h1>
                            <h4>Lorem, ipsum dolor sit amet consectetur adipisicing elit.</h4>
                            <Button  className={classes.custom_btn }  onClick={() => handleShow(values)}>See the Menu</Button>

                        </div>
                       </div>

                   </Col>
                   <Col md={6} sm={12}>
                   <div className={classes.card}>
                       <Image src={burger} fluid className={classes.card_img}/>
                       <div className={classes.card_content}>
                            <h1 className={classes.card_content_title}>Burgger</h1>
                            <h4>Lorem, ipsum dolor sit amet consectetur adipisicing elit.</h4>
                            <Button  className={classes.custom_btn }  onClick={() => handleShow(values)}>See the Menu</Button>

                        </div>
                       </div>


                   </Col>
               </Row>
           </Container>

        </div>
    )
}

export default CategoryGrid
