import React, { useState } from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import { Container , Row, Col, Image, Modal, Button  } from 'react-bootstrap';
import MenuData from '../MenuData/MenuData'

import classes from './MenuItem.module.css';

const allCatValues = [... new Set(MenuData.map((curElem) => curElem.category))];


const MenuItem = (props) => {
    const [items, setItems]= useState(MenuData);
    const [catItems, setCatItems] = useState(allCatValues);


    return (
        <div>
           <Container fluid>
               <Row>
                   <Col >
                        {
                            items.map((elemnt) =>{
                                const {
                                    id, name, image, description, price } = elemnt;

                                    return (
                                        <div className={classes.card}>
                                            <Image  fluid className={classes.menu_img}/>
                                            <h1 className={classes.main_title}>{name}<span></span></h1>
                                            <p>{description}</p>
                                            <h4>{price}</h4>
                                            <Button  className={classes.custom_btn }>Order</Button>
                                        </div>
                                    )
                            })
                        }
                   </Col>
               </Row>
           </Container>

        </div>
    )
}

export default MenuItem;
