import React, { useState } from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import { Offcanvas, Image } from 'react-bootstrap';
import classes from './OffCanvas.module.css';
import cart from "../../assets/cart-nav.svg";




const OffCanvas = (props) => {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
      <>

        <div className={classes.nav_cart}  onClick={handleShow}>
            <Image src={cart} fluid className={classes.nav_cart_img} width='5%'/>
            <p className={classes.badge}>{}</p>
        </div>

        <Offcanvas show={show} placement={'end'} onHide={handleClose}>
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Offcanvas</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            Some text as placeholder. In real life you can have the elements you
            have chosen. Like, text, images, lists, etc.
          </Offcanvas.Body>
        </Offcanvas>
      </>
    );
  }


export default OffCanvas
