import CartContext from '../../store/cart-context';
import 'bootstrap/dist/css/bootstrap.min.css';
import classes from './Cart.module.css';

import { useCart } from 'react-use-cart';
import { Container, Row, Col, Image } from 'react-bootstrap';


const Cart = (props) => {
  const {
    isEmpty,
    totalUniqueItems,
    items, 
    totalItems,
    cartTotal,
    updateItemQuantity,
    removeItem, 
    emptyCart,
  } = useCart();
  if (isEmpty) return <h1>Your Cart is Empty</h1>
  return (
    <div>
      <Container>
        <Row>
          {items.map((item, id) => {
            return (
              <Col sm={12} key={item.id}>
                <div className={classes.card}>
                  <div className={classes.menu_item}>
                    <Image src={item.image} fluid className={classes.menu_img}/>
                  </div>
                  <div className={classes.menu_item_inner}>
                    <div className="py-1"></div>
                    <h2 className="text-center">{item.name}</h2>
                    <h3 className="pb-3 text-center">Price: <b>{item.price}</b></h3>
                    <button className="btn btn-info ms-2" onClick={() => updateItemQuantity(item.id, item.quantity - 1)}>-</button>
                    <button className="btn btn-info ms-2" onClick={() => updateItemQuantity(item.id, item.quantity + 1)}>+</button>
                    <button className="btn btn-info ms-2" onClick={() => removeItem(item.id)}>Remove</button>
                    <p>Total ( {totalUniqueItems})</p>
                  </div>
                </div>
            </Col>
            )
          })}

        </Row>
      </Container>
    </div>
  )

}

export default Cart;
