import React, { useState } from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import { Container , Row, Col, Image, Modal, Button  } from 'react-bootstrap';
import MenuData from '../MenuData/MenuData'

import classes from './MenuItem.module.css';




const MenuItem = (props) => {

    return (
        <div>
            <Container fluid>
                <Row>
                    {
                        props.items.map((elemnt) =>{
                            const {
                                id, name, image, description, price } = elemnt;
                                return (
                                    <Col md={6} sm={12}>
                                        <div className={classes.card}>
                                            <div className={classes.menu_item}>
                                                <Image src={image} fluid className={classes.menu_img}/>
                                            </div>

                                            <h1 className={classes.main_title}><b>{name}</b></h1>
                                            <p className='px-5 mx-5'>{description}</p>
                                            <h4>Ptice: {price}</h4>
                                            <Button  className={classes.custom_btn }>Order</Button>
                                        </div>
                                    </Col>

                                )
                            })
                        }
                </Row>
            </Container>
        </div>
    )
}

export default MenuItem;
