import React, { useState } from 'react';

import 'bootstrap/dist/css/bootstrap.min.css';
import {  Image, Offcanvas, Button  } from 'react-bootstrap';

import chart from "../../assets/chart.svg";

import classes from './OffCanvasExample.module.css';

const ModalTest = () => {
    const values = [true, 'sm-down', 'md-down', 'lg-down', 'xl-down', 'xxl-down'];
    const [fullscreen, setFullscreen] = useState(true);
    const [show, setShow] = useState(false);

    function handleShow(breakpoint) {
      setFullscreen(breakpoint);
      setShow(true);
    }
}




const OffCanvasExample = () => {
    const [show, setShow] = useState(false);


    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>

      <Image src={chart} fluid onClick={handleShow} className={classes.nav_chart}/>


      <Offcanvas show={show} placement={'end'} onHide={handleClose} end>
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Offcanvas</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          Some text as placeholder. In real life you can have the elements you
          have chosen. Like, text, images, lists, etc.
        </Offcanvas.Body>
      </Offcanvas>
    </>
    )
}

export default OffCanvasExample;
