import React, { useState, useContext } from 'react';
import CartContext from '../../store/cart-context';
import 'bootstrap/dist/css/bootstrap.min.css';
import {  Image} from 'react-bootstrap';
import cart from "../../assets/cart-nav.svg";
import classes from './HeaderCartButton.module.css';


const HeaderCartButton = (props) => {

    const cartCtx = useContext(CartContext);
    const numberOfCartItems = cartCtx.item.reduce((curNumber, item) => {
      return curNumber + item.amount;
    }, 0);

    return ( 
        <div className={classes.nav_cart} onClick={props.onClick}>
            <Image src={cart} fluid className={classes.nav_cart_img}/>
            <p className={classes.badge}>{numberOfCartItems}</p>
        </div>
    )
}

export default HeaderCartButton;
